<?phpinclude('../../../config/config.inc.php');session_start();global $smarty;$main_id=$_POST['main_id'];$new_name=$_POST['new_name'];$quantid=explode(",",$_POST['items']);$chain_product=implode(",",array_unique($quantid));$gnew ="SELECT COUNT(*) as count FROM "._DB_PREFIX_."group_product WHERE duplicated_id='$main_id'";		$rque = Db::getInstance()->getValue($gnew);		if($rque['count'] >0)		{			$sql=Db::getInstance()->Execute("UPDATE "._DB_PREFIX_."group_product SET `child_product`='$chain_product' WHERE `duplicated_id` ='$main_id'");							if($sql)				{				echo "3";				}				else				{				echo "4";				}		}		else		{		$sql = Db::getInstance()->insert('group_product', array(									'product_name' => pSQL($new_name),									'parent_product' => pSQL($main_id),									'child_product' => pSQL($chain_product)															));			if($sql)			{					echo "1";			}			else			{			echo "2";			}		}	?>		